var mongoose = require('mongoose');
var http = require('http');


exports.MetaQuery = function (project, queryName, callback) {
    var m = mongoose.model("Project");
    m.findById(project, function (error, doc) {
        var url = "http://localhost:" + doc.SimulateCode + "/simulate/preparequery/" + queryName;
        console.log(url);

        var options = { host: "localhost", port: doc.SimulateCode, path: '/simulate/preparequery/' + queryName, method: 'GET' };
        var req1 = http.request(options, function (res1) {
            res1.setEncoding('utf8');
            res1.on('data', function (chunk) {
                //console.log(chunk);
                callback(chunk);
                return;
            });
        });
        req1.on('error', function (e) {
            callback({ IsValid: false, Errors: [{ ErrorMessage: e.message}] });
           // console.log('problem with request: ' + e.message);
        });
        req1.end();


    });
}
;
 